import { parse } from "@babel/parser";
import { AbstractMetric, IBlob, IModule, IntervalConfig } from "../types";
import { findPathInFileTree, getAllBlobsFromTree, returnMetricValueWithDesc } from "../utils";
import { traverse } from "@babel/types";

export default class Abstractness extends AbstractMetric<IModule> {
  readonly name = 'Abstractness';
  readonly info = 'Abstractness (Martin metric)';
  readonly scope = 'module';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetModulePath: string) {
    // interfaces also contribute to abstractness
    let totalNumberOfClassesAndInterfaces = 0;
    let numberOfAbstractClassesAndInterfaces = 0;

    const targetModule = findPathInFileTree(targetModulePath, program) as IModule;
    const filesInTargetModule: IBlob[] = getAllBlobsFromTree(targetModule, ['ts', 'js']);

    for (const file of filesInTargetModule) {
      const ast = parse(file.content, { 
        plugins: ['jsx', 'typescript', 'estree'], sourceType: 'module' 
      });

      traverse(ast, { 
        enter(node) {
          if (node.type === 'ClassDeclaration') {
            totalNumberOfClassesAndInterfaces++;
            if (node.abstract) numberOfAbstractClassesAndInterfaces++;
          }
          if (node.type === 'InterfaceDeclaration') {
            totalNumberOfClassesAndInterfaces++;
            numberOfAbstractClassesAndInterfaces++;
          }
      }});
    }

    return returnMetricValueWithDesc(numberOfAbstractClassesAndInterfaces / totalNumberOfClassesAndInterfaces, this._intervals);
  } 
}

import { AbstractMetric, IBlob, IModule, IntervalConfig } from "../types";
import { getAllBlobsFromTree, getAllModulesFromTree, returnMetricValueWithDesc } from "../utils";
import { parse } from '@babel/parser';
import { Identifier, traverse } from '@babel/types';

export default class AfferentCoupling extends AbstractMetric<IModule> {
  readonly name = 'Afferent coupling';
  readonly info = 'Afferent coupling (Martin metric)';
  readonly scope = 'module';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetModulePath: string) {
    let afferentCoupling = 0;
    const classesInTargetModule: string[] = [];

    const modules = getAllModulesFromTree(program);
    const targetModule = modules.find(el => el.path === targetModulePath);

    if (targetModule) {
      const _classesInTargetModule: IBlob[] = getAllBlobsFromTree(targetModule, ['ts', 'js']);

      for (const blob of _classesInTargetModule) {
        const ast = parse(blob.content, { 
          plugins: ['jsx', 'typescript', 'estree'], sourceType: 'module' 
        });

        traverse(ast, { 
          enter(node) {
            if (node.type === 'ExportNamedDeclaration' || 
              node.type === 'ExportDefaultDeclaration') {
              if (node.declaration?.type === 'ClassDeclaration') {
                classesInTargetModule.push(node.declaration.id?.name as string);
              }
            }
        }});
      }
    }

    for (const module of modules) {
      if (module.path !== targetModulePath) {
        const classesInModule = getAllBlobsFromTree(module, ['ts', 'js']);

        for (const blob of classesInModule) {
          const ast = parse(blob.content, { 
            plugins: ['jsx', 'typescript', 'estree'], sourceType: 'module' 
          });

          traverse(ast, { 
            enter(node) {
              if (node.type === 'ImportDeclaration' && node.source.value.includes(targetModulePath.split('/').at(-1) as string)) {
                for (const specifier of node.specifiers) {
                  if (specifier.type === 'ImportSpecifier' && classesInTargetModule.includes((specifier.imported as Identifier).name)) {
                    afferentCoupling++;
                  }
                  if (specifier.type === 'ImportDefaultSpecifier' && classesInTargetModule.includes((specifier.local as Identifier).name)) {
                    afferentCoupling++;
                  }
                }
              }
          }});
        }
      }
    }

    return returnMetricValueWithDesc(afferentCoupling, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { traverse } from '@babel/types';
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class CouplingBetweenObjectClasses extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Coupling Between Object Classes';
  readonly info = 'Coupling Between Object Classes';
  readonly scope = 'class';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const importedClasses: {name: string, isUsed: boolean}[] = [];
    let isClass = false;
    let numberOfUsages = 0;

    traverse(program, { 
      enter(node) {
        if (node.type === 'ImportDeclaration') {
          for (const item of node.specifiers) {
            if (item.local.name[0] === item.local.name[0].toUpperCase()) {
              importedClasses.push({name: item.local.name, isUsed: false});
            }
          }
        }

        if (node.type === 'ClassDeclaration') {
          isClass = true;
        }
    }});

    if (isClass) {
      traverse(program, { 
        enter(node) {
          if (node.type === 'Identifier') {
            const indexInImportedClasses = importedClasses.findIndex(elem => elem.name === node.name && !elem.isUsed);
            if (indexInImportedClasses !== -1) {
              numberOfUsages++;
              importedClasses[indexInImportedClasses].isUsed = true;
            }
          }
      }});
    }

    return returnMetricValueWithDesc(numberOfUsages, this._intervals);
  } 
}

import { AbstractMetric, IBlob, IModule, IntervalConfig } from "../types";
import { findPathInFileTree, returnMetricValueWithDesc } from "../utils";
import { parse } from '@babel/parser';
import { traverse } from '@babel/types';

export default class DepthOfInheritanceTree extends AbstractMetric<IModule> {
  readonly name = 'Depth Of Inheritance Tree';
  readonly info = 'Depth Of Inheritance Tree';
  readonly scope = 'class';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetClassPath: string) {
    const depthOfInheritanceTree = getDepth(program, targetClassPath, 0);

    function getDepth(program: IModule, targetClassPath: string, depth: number) {
      let _depth = depth; 
      const file = findPathInFileTree(targetClassPath, program);

      if (file?.type !== 'blob') {
        throw Error("Provided path is not a file. Please, specify path to the file");
      }

      const ast = parse((file as IBlob).content, { 
        plugins: ['jsx', 'typescript', 'estree'], sourceType: 'module' 
      });

      let parentClassname: string | null = null;
      traverse(ast, { 
        enter(node) {
          if (node.type === 'ClassDeclaration' && node.superClass) {
            //@ts-expect-error ignore
            parentClassname = node.superClass.name;
          }
      }});

      if (parentClassname) {
        traverse(ast, { 
          enter(node) {
            if (node.type === 'ImportDeclaration') {
              for (const specifier of node.specifiers) {
                if (specifier.local.name === parentClassname) {
                  let absolutePath = '';
                  const relativePath = node.source.value.split('/');
                  const _relativePath = [...relativePath];
                  const currentDir = targetClassPath.split('/');
                  const extension = '.' + currentDir.at(-1)?.split('.').at(-1);

                  for (let i = 0; i < relativePath.length; i++) {
                    if (relativePath[i] === '.') {
                      currentDir.pop();
                      currentDir.push(parentClassname + extension);
                      absolutePath = currentDir.join('/');
                      break;
                    } else if (relativePath[i] === '..') {
                      if (i === 0) currentDir.pop();
                      currentDir.pop();
                      _relativePath.shift();
                    } else {
                      absolutePath = currentDir.join('/') + '/' + _relativePath.join('/') + extension;
                      break;
                    }
                  }

                  _depth = getDepth(program, absolutePath, _depth + 1);
                }
                break;
              }
            }
        }});
      }

      return _depth;
    }    

    return returnMetricValueWithDesc(depthOfInheritanceTree, this._intervals);
  } 
}

import { AbstractMetric, IBlob, IModule, IntervalConfig } from "../types";
import { findPathInFileTree, getAllBlobsFromTree, returnMetricValueWithDesc } from "../utils";
import { parse } from '@babel/parser';
import { traverse } from '@babel/types';

export default class EfferentCoupling extends AbstractMetric<IModule> {
  readonly name = 'Efferent coupling';
  readonly info = 'Efferent coupling (Martin metric)';
  readonly scope = 'module';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetModulePath: string) {
    let efferentCoupling = 0;

    const module = findPathInFileTree(targetModulePath, program); 

    if (module) {
      const _classesInTargetModule: IBlob[] = getAllBlobsFromTree(module as IModule, ['ts', 'js']);

      for (const blob of _classesInTargetModule) {
        const ast = parse(blob.content, { 
          plugins: ['jsx', 'typescript', 'estree'], sourceType: 'module' 
        });

        const cdUpsToGoOutsideModule = blob.path.split('/').length - targetModulePath.split('/').length;

        traverse(ast, { 
          enter(node) {
            if (node.type === 'ImportDeclaration') {
              if (node.source.value.includes('@') || (node.source.value.match(/..\//g) || []).length >= cdUpsToGoOutsideModule) {
                efferentCoupling++;
              }
            }
        }});
      }
    }

    return returnMetricValueWithDesc(efferentCoupling, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { returnMetricValueWithDesc } from "../utils";
import TotalNumberOfOperands from './TotalNumberOfOperands';
import TotalNumberOfOperators from './TotalNumberOfOperators';
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';

export default class ImplemetationLength extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Implementation length';
  readonly info = 'Implementation length = TotalNumberOfOperands + TotalNumberOfOperators';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const value = new TotalNumberOfOperands(this._intervals).run(program).value + new TotalNumberOfOperators(this._intervals).run(program).value;
    return returnMetricValueWithDesc(value, this._intervals);
  } 
}

import { AbstractMetric, IModule, IntervalConfig } from "../types";
import { returnMetricValueWithDesc } from "../utils";
import AfferentCoupling from "./AfferentCoupling";
import EfferentCoupling from "./EfferentCoupling";

export default class Instability extends AbstractMetric<IModule> {
  readonly name = 'Instability';
  readonly info = 'Instability (Martin metric)';
  readonly scope = 'module';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetModulePath: string) {
    const afferentCoupling = new AfferentCoupling(this._intervals).run(program, targetModulePath).value;
    const efferentCoupling = new EfferentCoupling(this._intervals).run(program, targetModulePath).value;
    return returnMetricValueWithDesc(efferentCoupling > 0 ? (efferentCoupling / (efferentCoupling + afferentCoupling)) : 0, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { traverse } from '@babel/types';
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class LackOfCohesionOfMethods extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Lack Of Cohesion Of Methods';
  readonly info = 'Lack Of Cohesion Of Methods';
  readonly scope = 'class';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const methods: any[] = [];
    const methodsPairs = [];
    let shareMutualPropsCounter = 0;
    const attributes = [];

    traverse(program, { 
      enter(node) {
        if (node.type === 'ClassDeclaration') {
          for (const item of node.body.body) {
            if (item.type === 'ClassProperty') {
              attributes.push(item);
            }

            //@ts-expect-error ignore
            if (item.type === 'MethodDefinition') {
              methods.push(item);
            }
          }
        }
    }});

    for (let i = 0; i < methods.length - 1; i++) {
      for (let j = i + 1; j < methods.length; j++) {
        methodsPairs.push([methods[i], methods[j]]);
      }
    }

    for (const item of methodsPairs) {
      const propsInFirstMethod = new Set<string>();
      const propsInSecondMethod = new Set<string>();
      traverse(item[0].value, { 
        enter(node) {
          //@ts-expect-error ignore
          if (node.object && node.property && node.object.type === 'ThisExpression') {
            //@ts-expect-error ignore
            propsInFirstMethod.add(node.property.name);
          }
      }});
      traverse(item[1].value, { 
        enter(node) {
          //@ts-expect-error ignore
          if (node.object && node.property && node.object.type === 'ThisExpression') {
            //@ts-expect-error ignore
            propsInSecondMethod.add(node.property.name);
          }
      }});

      if ([...propsInFirstMethod].filter(el => propsInSecondMethod.has(el)).length > 0) {
        shareMutualPropsCounter++;
      }
    }

    const result = methodsPairs.length - (shareMutualPropsCounter * 2);

    return returnMetricValueWithDesc(result <= 0 ? 0 : result, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';
import ProgramVolume from "./ProgramVolume";
import McCabeCC from "./McCabeCC";
import { returnMetricValueWithDesc } from "../utils";

export default class MaintainabilityIndex extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Maintainability Index';
  readonly info = 'Maintainability Index proposed by Microsoft https://learn.microsoft.com/en-us/visualstudio/code-quality/code-metrics-maintainability-index-range-and-meaning?view=vs-2022';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const value = Math.max(0, 
      (171 - 5.2 * 
        Math.log(new ProgramVolume(this._intervals).run(program).value) - 0.23 * 
        new McCabeCC(this._intervals).run(program).value - 16.2 * 
        Math.log(Number(program.program.loc?.end.line) - Number(program.program.loc?.start.line))) * 100 / 171);

    return returnMetricValueWithDesc(value, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from '../types';
import * as Styx from 'styx';
import { returnMetricValueWithDesc } from '../utils';
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';

export default class McCabeCC extends AbstractMetric<ParseResult<File>> {
  readonly name = 'McCabe cylomatic complexity';
  readonly info = 'm - number of edges\nm - number of nodes\nvalue - cylomatic complexity (Z)';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const programFlow = Styx.parse(program.program);
    const m = programFlow.flowGraph.edges.length, n = programFlow.flowGraph.nodes.length;
    const result = m - n + 2;
    return returnMetricValueWithDesc(result, this._intervals);
  }
}

import { AbstractMetric, IModule, IntervalConfig } from "../types";
import Instability from "./Instability";
import Abstractness from "./Abstractness";
import { returnMetricValueWithDesc } from "../utils";

export default class NormalizedDistanceFromMainSequence extends AbstractMetric<IModule> {
  readonly name = 'Normalized Distance From Main Sequence';
  readonly info = 'Normalized distance from main sequence (Martin metric)';
  readonly scope = 'module';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetModulePath: string) {
    const instability = new Instability(this._intervals).run(program, targetModulePath).value;
    const abstractness = new Abstractness(this._intervals).run(program, targetModulePath).value;
    return returnMetricValueWithDesc(Math.abs(instability + abstractness - 1), this._intervals);
  } 
}

import { AbstractMetric, IModule, IntervalConfig } from "../types";
import { getAllBlobsFromTree, returnMetricValueWithDesc } from "../utils";
import { parse } from '@babel/parser';
import { traverse } from '@babel/types';

export default class NumberOfChild extends AbstractMetric<IModule> {
  readonly name = 'Number Of Child';
  readonly info = 'Number of children for the given class';
  readonly scope = 'module';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: IModule, targetClassPath: string) {
    let numberOfChildren = 0;
    const targetClassName = targetClassPath.split('/').at(-1)?.split('.').at(0);
    const blobs = getAllBlobsFromTree(program, ['ts', 'js']);

    for (const blob of blobs) {
      if (blob.path !== targetClassPath) {
        const ast = parse(blob.content, { 
          plugins: ['jsx', 'typescript', 'estree'], sourceType: 'module' 
        });

        traverse(ast, { 
          enter(node) {
            if (node.type === 'ImportDeclaration') {
              let isClassImported = false;
              for (const specifier of node.specifiers) {
                if (specifier.local.name === targetClassName) {
                  let absolutePath = '';
                  const relativePath = node.source.value.split('/');
                  const _relativePath = [...relativePath];
                  const currentDir = blob.path.split('/');
                  const extension = '.' + currentDir.at(-1)?.split('.').at(-1);

                  for (let i = 0; i < relativePath.length; i++) {
                    if (relativePath[i] === '.') {
                      currentDir.pop();
                      currentDir.push(targetClassName + extension);
                      absolutePath = currentDir.join('/');
                      if (absolutePath === targetClassPath) isClassImported = true;
                      break;
                    } else if (relativePath[i] === '..') {
                      if (i === 0) currentDir.pop();
                      currentDir.pop();
                      _relativePath.shift();
                    } else {
                      absolutePath = currentDir.join('/') + '/' + _relativePath.join('/') + extension;
                      if (absolutePath === targetClassPath) isClassImported = true;
                      break;
                    }
                  }
                }
              }

              if (isClassImported) {
                traverse(ast, { 
                  enter(node) {
                    if (node.type === 'ClassDeclaration' && node.superClass) {
                      //@ts-expect-error ignore
                      if (targetClassName === node.superClass.name) numberOfChildren++;
                    }
                }});
              }
            }
        }});
      }
    }

    return returnMetricValueWithDesc(numberOfChildren, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { ParseResult } from '@babel/parser';
import { ArrowFunctionExpression, File, FunctionDeclaration, traverse } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class NumberOfInputOutputParameters extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Number of input & output parameters';
  readonly info = 'Number of input & output parameters in the function';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    let numberOfInputParams = 0;
    let isReturn = null;

    const functions: Array<FunctionDeclaration | ArrowFunctionExpression> = [];

    traverse(program, {
      enter(node) {
        if (node.type === 'FunctionDeclaration' || node.type === 'ArrowFunctionExpression')
          functions.push(node);
        else if (node.type === 'ReturnStatement')
          isReturn = true;
      }
    });

    for (const func of functions) {
      numberOfInputParams += func.params.length;
    }

    return returnMetricValueWithDesc(numberOfInputParams + (isReturn ? 1 : 0), this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { ParseResult } from '@babel/parser';
import { traverse, File } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class NumberOfMethods extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Number Of Methods';
  readonly info = 'Number Of Methods';
  readonly scope = 'class';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {

    let MethodsCount = 0;

    traverse(program, { 
      enter(node) {
        if (node.type === 'ClassBody') {
          for (const item of node.body) {
            //@ts-expect-error ignore
            if (item.type === 'MethodDefinition') MethodsCount++;
          }
        }
    }});
    return returnMetricValueWithDesc(MethodsCount, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { returnMetricValueWithDesc } from "../utils";
import NumberOfInputOutputParameters from './NumberOfInputOutputParameters';
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';

export default class PotentialProgramVolume extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Potential program volume';
  readonly info = 'Potential program volume - ideal volume of the program (compared to ProgramVolume - real volume of the program)';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const numberOfInputOutputParameters = new NumberOfInputOutputParameters(this._intervals).run(program).value;
    return returnMetricValueWithDesc((numberOfInputOutputParameters + 2) * Math.log2(numberOfInputOutputParameters + 2), this._intervals);
  } 
}

