import { AbstractMetric, IntervalConfig } from "../types";
import { returnMetricValueWithDesc } from "../utils";
import ProgramEffort from "./ProgramEffort";
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';

export default class ProgrammingTime extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Programming Time';
  readonly info = 'Programming Time = ProgramEffort / Straud number (average value of Straud number is 18). Represents hours required to write a given program';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    return returnMetricValueWithDesc(new ProgramEffort(this._intervals).run(program).value / 18, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class TotalNumberOfOperands extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Total number of operands';
  readonly info = 'Total number of operands in the program';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    let operandsCount = 0;
    if (program.tokens) {
      for (const token of program.tokens) {
        if (token.type.keyword === undefined 
          && token.value 
          && (token.type.label === 'name' || token.type.label === 'string') 
          && token.value !== 'let') operandsCount++;
      }
    }
    return returnMetricValueWithDesc(operandsCount, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class TotalNumberOfOperators extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Total number of operators';
  readonly info = 'Total number of operators in the program';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    let operatorsCount = 0;
    if (program.tokens) {
      for (const token of program.tokens) {
        if ((token.type.keyword 
          || token.type.binop)
          || (token.type.label !== 'name' && token.type.label !== 'string')
          || token.value === 'let') operatorsCount++;
      }
    }
    return returnMetricValueWithDesc(operatorsCount, this._intervals);
  } 
}

import { AbstractMetric, IntervalConfig } from "../types";
import { ParseResult } from '@babel/parser';
import { File } from '@babel/types';
import { returnMetricValueWithDesc } from "../utils";

export default class TotalNumberOfUniqueOperands extends AbstractMetric<ParseResult<File>> {
  readonly name = 'Total number of unique operands';
  readonly info = 'Total number of unique operands in the program';
  readonly scope = 'function';

  constructor(config: IntervalConfig[]) {
    super(config);
  }

  public run(program: ParseResult<File>) {
    const uniqueOperands: string[][] = [];
    if (program.tokens) {
      for (const token of program.tokens) {
        if (token.type.keyword === undefined 
          && token.value 
          && (token.type.label === 'name' || token.type.label === 'string') 
          && token.value !== 'let' 
          && !uniqueOperands.find((elem) => elem[0] === token.value && elem[1] === token.type.label)) {
          uniqueOperands.push([token.value, token.type.label]);
        } 
      }
    }
    return returnMetricValueWithDesc(uniqueOperands.length, this._intervals);
  } 
}

