import { File } from "@babel/types";
import { ParseResult } from '@babel/parser';

abstract class AbstractMetric<C extends ParseResult<File> | IModule> {
  abstract readonly name: string;
  abstract readonly info: string;
  abstract readonly scope: 'module' | 'class' | 'function';
  readonly _intervals: IntervalConfig[];

  constructor(config: IntervalConfig[]) {
    this._intervals = config;
  }

  public abstract run(program: C, targetClassPath?: string, targetModulePath?: string, targetFunctionPath?: string): IMetricResult;
}

interface IMetricResult {
  value: number;
  description?: string;
}

interface IFileTreeNode {
  type: 'tree' | 'blob';
  name: string;
  path: string;
}

interface IBlob extends IFileTreeNode {
  content: string;
}

interface IModule extends IFileTreeNode {
  trees: IModule[];
  blobs: IBlob[];
}

interface IConfig {
  rootDir: string;               // specify directory that will be parsed and analyzed
  extentions: Array<'ts' | 'js'>; // parser will check only specific file extentions
  metrics: IMetricConfig[];       // metrics that should be calculated with specified ranges and limits
}

interface IMetricConfig {
  name: string;                 // metric name, must match classname of corresponding metric
  intervals: IntervalConfig[];  // ORDERED list of intervals that allows to evalutate metric value
}

interface IntervalConfig {
  valueMin: number | null;
  valueMax: number | null; 
  description: string;
}

interface IFinalMetricResult {
	metricName: string
	resultScope: 'module' | 'class' | 'function'
	subjectPath: string
	value: number
	description?: string
}

interface IBlobMetricsResults extends IFileTreeNode {
	metricResults: IFinalMetricResult[];
}

interface ITreeMetricsResults extends IBlobMetricsResults {
	trees: ITreeMetricsResults[];
	blobs: IBlobMetricsResults[];
}

export type { IMetricResult, IFileTreeNode, IBlob, IModule, IConfig, IMetricConfig, IntervalConfig, ITreeMetricsResults, IBlobMetricsResults, IFinalMetricResult };
export { AbstractMetric };

import { IBlob, IMetricResult, IModule, ITreeMetricsResults, IntervalConfig } from "./types";

export function findPathInFileTree(path: string, tree: IModule): IModule | IBlob | null {
  let result = null;
  if (tree.path === path) {
    return tree;
  } else {
    if (!result) {
      for (const node of tree.blobs) {
        if (node.path === path) return node;
      }
    }

    if (tree.trees) {
      for (const node of tree.trees) {
        result = findPathInFileTree(path, node);
        if (result) break;
      }
    }
  }
  return result;
}

export function getAllBlobsFromTree(tree: IModule, extentions: string[]): IBlob[] {
  let result: IBlob[] = [];

  if (tree) {
    if (tree.trees) {
      for (const node of tree.trees) {
        result = result.concat(getAllBlobsFromTree(node, extentions));
      }
    }

    for (const node of tree.blobs) {
      if (extentions.includes(node.name.split('.').at(-1) as string)) {
        result.push(node);
      }
    }
  }

  return result;
}

export function getAllModulesFromTree(tree: IModule): IModule[] {
  let result: IModule[] = [tree];
  if (tree && tree.trees) {
    for (const node of tree.trees) {
      result = result.concat(getAllModulesFromTree(node));
    }
  }
  return result;
}

export function returnMetricValueWithDesc(value: number, intervals: IntervalConfig[]): IMetricResult {
  for (const interval of intervals) {
    if (value >= (interval.valueMin === null ? Number.NEGATIVE_INFINITY : interval.valueMin) 
      && value <= (interval.valueMax === null ? Number.POSITIVE_INFINITY : interval.valueMax)) {
      return { value, description: interval.description };
    }
  }
  return { value, description: '' };
}

const STRIP_COMMENTS = /((\/\/.*$)|(\/\*[\s\S]*?\*\/))/mg;
const ARGUMENT_NAMES = /([^\s,]+)/g;
export function getParamNames(func: (...args: any) => any) {
  const fnStr = func.toString().replace(STRIP_COMMENTS, '');
  let params: any = fnStr.slice(fnStr.indexOf('(')+1, fnStr.indexOf(')')).match(ARGUMENT_NAMES);
  if (params === null) params = [];
  return params;
}

export function inputTreeToOutputTree(inputTree: IModule): ITreeMetricsResults {
  return {
    type: inputTree.type,
    name: inputTree.path,
    path: inputTree.path,
    metricResults: [],
    trees: inputTree.trees.map(elem => inputTreeToOutputTree(elem)),
    blobs: inputTree.blobs.map(elem => {
      return {
        type: elem.type,
        name: elem.name,
        path: elem.path,
        metricResults: []
      }
    })
  };
}

