export default class NumberOfHansonOperators implements IMetric {
  private _name = 'Number of Hanson operators';
  private _info =
    'Number of operators as defined by Hanson https://dl.acm.org/doi/pdf/10.1145/954373.954375';
  private _scope = 'any';

  public get name() {
    return this._name;
  }

  public get info() {
    return this._info;
  }

  public get scope() {
    return this._scope as any;
  }

  public run(program: ParseResult<File>) {
    let operatorsCount = 0;
    traverse(program, { 
      enter(node) {
        if (
          ['BinaryExpression', 'AssignmentExpression', 'CallExpression'].includes(
            node.type
          )
        ) {
          operatorsCount++;
        }
    }});

    return { value: operatorsCount };
  }
}

import { IMetric } from "../types";
import { ParseResult } from '@babel/parser';
import { ArrowFunctionExpression, File, FunctionDeclaration, traverse } from '@babel/types';

export default class NumberOfInputOutputParameters implements IMetric {
  private _name = 'Number of input & output parameters';
  private _info = 'Number of input & output parameters';
  private _scope = 'function';

  public get name() {
    return this._name;
  }

  public get info() {
    return this._info;
  }

  public get scope() {
    return this._scope as any;
  }

  public run(program: ParseResult<File>) {
    let numberOfInputParams = 0;
    let isReturn = null;

    const functions: Array<FunctionDeclaration | ArrowFunctionExpression> = [];

    traverse(program, {
      enter(node) {
        if (node.type === 'FunctionDeclaration' || node.type === 'ArrowFunctionExpression')
          functions.push(node);
        else if (node.type === 'ReturnStatement')
          isReturn = true;
      }
    });

    for (const func of functions) {
      numberOfInputParams += func.params.length;
    }

    return { value: numberOfInputParams + (isReturn ? 1 : 0) };
  } 
}

