  }

  public get info() {
    return this._info;
  }

  public get scope() {
    return this._scope as any;
  }

  public run(program: ParseResult<File>) {

    let MethodsCount = 0;

    traverse(program, { 
      enter(node) {
        if (node.type === 'ClassBody') {
          for (const item of node.body) {
            //@ts-ignore
            if (item.type === 'MethodDefinition') MethodsCount++;
          }
        }
    }});

    return { value: MethodsCount };
  } 
}

